import axios from "axios";
import React, { useEffect, useState } from "react";
import './aboutpool.css';

function AllPool({ allPool }) {
  const loggedinUser = parseInt(localStorage.getItem("logged"));
  const [selectedRow, setSelectedRow] = useState(null);

  const [values, setValues] = useState({
    poolid: 0,
    loginid: loggedinUser,
  });

  const handleClick = (row) => {
    setSelectedRow(row);
  };
  console.log(allPool);
  useEffect(() => {
    if (selectedRow != null) {
      setValues({ poolid: selectedRow.poolId, loginid: loggedinUser });
      console.log(values);
    }
  }, [selectedRow]);

  const handleCheckIn = () => {
    axios
      .post("http://localhost:8081/checkin", values)
      .then((res) => {
        console.log(res);
      })
      .catch((error) => {
        console.log(error.response.data);
        // toast.error(error.response.data.error, {
        //   position: "top-center",
        // });
      });
  };

  return (
    <>
    <div className="allpooldiv">
      {/* <div>These are available pools</div><br /><br /><br /><br /><br /> */}
      <div className="mainpool">
        {allPool.map((item) => (
          {/* <div className="poolintro" key={item.id} onClick={() => handleClick(item)}>
            <div className="outerpara"><b>Pool Created By:</b>{" "}{item.poolBy}</div>
            <div className="outerpara"><b>Pool Starting From:</b>{" "}{item.poolFrom}</div>
            <div className="outerpara"><b>Pool Date:</b>{" "}{item.poolDate}</div>
          </div> */}









          
        ))}
      </div>
      <div className="selecteddiv">
      {selectedRow && (
        <div>
        <p><b>Pool Created By: </b>{"\t"}{selectedRow.poolBy}</p>
          <p><b>Pool From: </b>{"\t"}{selectedRow.poolFrom}</p>
          <p><b>Pool To: </b>{"\t"}{selectedRow.poolTo}</p>
          <p><b>Pool Date: </b>{"\t"}{selectedRow.poolDate}</p>
          <p><b>Pool Seats: </b>{"\t"}{selectedRow.poolSeats}</p>
          <p><b>Pool Available Seats: </b>{"\t"}{selectedRow.availableSeats}</p>
          {/* <p>{loggedinUser}</p> */}
          {/* <p>
            {selectedRow.members.map((e) => {
              return <div>{e}</div>;
            })}
          </p> */}
          {selectedRow.members.includes(loggedinUser) === true ? (
            <button disabled>You have already checked in </button>
          ) : selectedRow.availableSeats > 0 ? (
            <button onClick={handleCheckIn}>Check In</button>
          ) : (
            <button disabled>Full </button>
          )}
        </div>
      )}
      </div>
      </div>
    </>
  );
}

export default AllPool;
