import axios from "axios";
import React, { useEffect, useState } from "react";
import "./aboutpool.css";
import car1 from "./car20.png";
import car2 from "./car23.png";
import car3 from "./bike5.png";
import car4 from "./car24.png";
import car5 from "./bike3.png";
import car11 from "./car14.jpg";

import { AiOutlineArrowRight } from "react-icons/ai";

function AllPool({ allPool }) {
  const loggedinUser = parseInt(localStorage.getItem("logged"));
  const [selectedRow, setSelectedRow] = useState(null);
  const images = [car1, car2, car3, car4, car5];
  const [search, setSearch] = useState(null);
  const [searchPool, setSearchPool] = useState({
  searchfrom:"",
  searchto:"",
  searchdate:""
});

  const [searcharray, setSearchArray] = useState([]);
  const [values, setValues] = useState({
    poolid: 0,
    loginid: loggedinUser,
  });

  const handleClick = (row) => {
    setSelectedRow(row);
  };
  console.log(allPool);
  useEffect(() => {
    if (selectedRow != null) {
      setValues({ poolid: selectedRow.poolId, loginid: loggedinUser });
      console.log(values);
    }
  }, [selectedRow]);

  const handleCheckIn = () => {
    axios
      .post("http://localhost:8081/checkin", values)
      .then((res) => {
        console.log(res);
        window.location.reload(true)

      })
      .catch((error) => {
        console.log(error.response.data);
        // toast.error(error.response.data.error, {
        //   position: "top-center",
        // });
      });
  };

  const handleSearch = (event) => {
    setSearchPool((prev) => ({
      ...prev,
      [event.target.name]: [event.target.value],
    }));
  };

  return (
    <>
      <div className="allpooldiv">

<div style={{display:"flex",flex:1,justifyContent:"center", alignItems:"center",marginTop:"2%"}}>      
<div class="form-group mx-sm-3 mb-2" style={{width:"20%"}}>
    <input type="text" class="form-control" placeholder="Search From" name="searchfrom" onChange={handleSearch}/>
  </div>
  <div class="form-group mx-sm-3 mb-2" style={{width:"20%"}}>
    <input type="text" class="form-control"  placeholder="Search To" name="searchto" onChange={handleSearch}/>
  </div>
  <div class="form-group mx-sm-3 mb-2" style={{width:"20%"}}>
    <input type="date" class="form-control" placeholder="Search Date" name="searchdate" onChange={handleSearch}/>
  </div>
  <button class="btn btn-primary" style={{marginBottom:"0.5%",marginLeft:"3%"}}>Search</button>

</div>
        {/* <div>These are available pools</div><br /><br /><br /><br /><br /> */}
{search === null ? 
  <div className="mainpool">
          {allPool.map((item, index) => (
            <div
              className="poolintro"
              key={item.id}
              onClick={() => handleClick(item)}
            >
              {/* <div className="toppara">{item.poolBy}'s Pool</div> */}

              {item.availableSeats > 2 ? 
                <div className="toppara2">{item.poolBy}'s Pool</div>:(
                item.availableSeats === 0 ? 
                <div className="toppara">{item.poolBy}'s Pool</div>:
                <div className="toppara3">{item.poolBy}'s Pool</div>
              )}

              <img src={images[index % 5]} style={{ height: "60%" }} />
              <div className="outerpara">
                <b>
                  {" "}
                  {item.poolFrom} <AiOutlineArrowRight /> {item.poolTo}
                </b>
              </div>
            </div>
          ))}
        </div>
:
<div className="mainpool">
          {allPool.map((item, index) => (
            <div
              className="poolintro"
              key={item.id}
              onClick={() => handleClick(item)}
            >
              {/* <div className="toppara">{item.poolBy}'s Pool</div> */}

              {item.availableSeats > 2 ? 
                <div className="toppara2">{item.poolBy}'s Pool</div>:(
                item.availableSeats === 0 ? 
                <div className="toppara">{item.poolBy}'s Pool</div>:
                <div className="toppara3">{item.poolBy}'s Pool</div>
              )}

              <img src={images[index % 5]} style={{ height: "60%" }} />
              <div className="outerpara">
                <b>
                  {" "}
                  {item.poolFrom} <AiOutlineArrowRight /> {item.poolTo}
                </b>
              </div>
            </div>
          ))}
        </div>



}
        <div className="selecteddiv">
          {selectedRow && (
            <div className="modal-background">
              <div className="modal-container">
              <div className="poolimg">
              <img src={car11} className="leftimg" />
              </div>
                <div className="pooldetails">
                  {/* <div className="poolvalues">
                    <b>Pool Created By: </b>
                    {"\t"}
                    {selectedRow.poolBy}
                  </div> */}
                  <div className="poolvalues">
                    <span>
                      <b>Pool From: <br/>{selectedRow.poolFrom}</b>
                    </span>

                    <span>
                      <b>Pool To: <br/>{selectedRow.poolTo}</b>
                    </span>
                  </div>

                  <div className="poolvalues">
                    <b>Pool Date:<br/>{selectedRow.poolDate}</b>

                    <b>Pool Time:<br/> {selectedRow.poolTime} </b>
                  </div>

                  <div className="poolvalues">
                    <b>Pool Seats: {selectedRow.poolSeats} </b>
                    
                 
                    <b>Available Seats: {selectedRow.availableSeats}</b>
                    
                  </div>
                <div className="poolbutton">
                  {selectedRow.members.length > 0 &&
                  selectedRow.members.includes(loggedinUser) === true ? (
                    <button class="btn btn-success">
                      Already checked in{" "}
                    </button>
                  ) : selectedRow.availableSeats > 0 ? (
                    <button class="btn btn-primary" onClick={handleCheckIn}>
                      Please Check In {" "}
                    </button>
                  ) : (
                    <button class="btn btn-danger">
                      No Seats Available{" "}
                    </button>
                  )}
                  <button
                    class="btn btn-danger"
                    style={{ width: "20%" }}
                    onClick={() => setSelectedRow(null)}
                  >
                    Close
                  </button>
                </div>
                </div>

              </div>
            </div>
          )}
        </div>
      </div>
    </>
  );
}

export default AllPool;
