import { all } from 'axios';
import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'

function PoolHome() {
    const navigate = useNavigate();
    const [section, setSection] = useState("1");
    const [allPool, setallPool] = useState([]);
    const [selectedRow, setSelectedRow] = useState(null);

    const loggedinUser = localStorage.getItem("logged");
    console.log(loggedinUser);
    useEffect(() => {
      fetch("http://localhost:8081/allpool")
        .then(response => response.json())
        .then(data => setallPool(data))
        .catch(error => console.log(error));
    }, []);
    const handleClick = (row) => {
      setSelectedRow(row);
    };
  return (
    <>
    <button onClick={()=>{setSection("1")}}>Available Pools</button>
    <button onClick={()=>{setSection(false)}}>My Pools</button>
    <button onClick={()=>{setSection(fal)}}>Joined Pools</button>


<div>
  {section ? 
  <>
    <div>These are available pools</div>
    <div>
      {allPool.map(item => (
        <div key={item.id} onClick={() => handleClick(item)}>
          <p>{item.name}</p>
          <p>{item.email}</p>
        </div>
      ))}
    </div>
    {selectedRow && (
        <div>
          <p>{selectedRow.name}</p>
          <p>{selectedRow.email}</p>
          <p>{selectedRow.password}</p>
          <p>{loggedinUser}</p>
        </div>
      )}
  </>
  :
  <>
    <div>
      These are my pools
    </div>
      </>
  }
</div>

      <div>
        <button onClick={()=>{navigate('/signup')}}>LOGOUT</button>
    </div>
    </>
  )
}

export default PoolHome
