              navigate("/signup");

            }}
          >
            LOGOUT
          </button>

      </div>
    

    
        <div>
          {(() => {
            switch (section) {
              case "1":
                return (
                  <>
                    {allPool.length > 0 ? <AllPool allPool={allPool} /> : null}
                  </>
                );

              case "2":
                return (
                  <>
                    {allPool.length > 0 ? <MyPool allPool={allPool} /> : null}
                  </>
                );

              case "3":
                return (
                  <>
                    {allPool.length > 0 ? <JoinedPool allPool={allPool} /> : null}
                  </>
                );
                case "4":
                return (
                  <>
                    <PoolForm />
                  </>
                );
              default:
                return (
                  <>
                    {allPool.length > 0 ? <AllPool allPool={allPool} /> : null}
                  </>
                );
            }
          })()}
        </div>

        <div>
          
        </div>
      </div>
    </>
  );
}

export default PoolHome;
