import { all } from 'axios';
import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'

function PoolHome() {
    const navigate = useNavigate();
    const [section, setSection] = useState(1);
    const [allPool, setallPool] = useState([]);
    const [selectedRow, setSelectedRow] = useState(null);

    const loggedinUser = localStorage.getItem("logged");
    console.log(section);
    useEffect(() => {
      fetch("http://localhost:8081/allpool")
        .then(response => response.json())
        .then(data => setallPool(data))
        .catch(error => console.log(error));
    }, []);
    const handleClick = (row) => {
      setSelectedRow(row);
    };
  return (
    <>
    <button onClick={()=>{setSection("1")}}>Available Pools</button>
    <button onClick={()=>{setSection("2")}}>My Pools</button>
    <button onClick={()=>{setSection("3")}}>Joined Pools</button>


{/* <div>
  {section ? 
  <>
    <div>These are available pools</div>
    <div>
      {allPool.map(item => (
        <div key={item.id} onClick={() => handleClick(item)}>
          <p>{item.name}</p>
          <p>{item.email}</p>
        </div>
      ))}
    </div>
    {selectedRow && (
        <div>
          <p>{selectedRow.name}</p>
          <p>{selectedRow.email}</p>
          <p>{selectedRow.password}</p>
          <p>{loggedinUser}</p>
        </div>
      )}
  </>
  :
  
  }
</div> */}


<div>
{console.log("entered")}
  {(()=>{
    switch(section){
      case 1:
      console.log("section 1")
       return(
        <>
       <div>These are available pools</div>
    <div>
      {allPool.map(item => (
        <div key={item.id} onClick={() => handleClick(item)}>
          <p>{item.name}</p>
          <p>{item.email}</p>
        </div>
      ))}
    </div>
    {selectedRow && (
        <div>
          <p>{selectedRow.name}</p>
          <p>{selectedRow.email}</p>
          <p>{selectedRow.password}</p>
          <p>{loggedinUser}</p>
        </div>
       
      )}
      </>
       );
       case "2":
      return(
        <>
    <div>
      These are my pools
    </div>
      </>
      );

      case "3":
       return(
        <>
    <div>
      These are my joined pools
    </div>
      </>
       );
       default:
        return (
          <>
            <div>Hello defult</div>
          </>
        );
    }
  })}
</div>


      <div>
        <button onClick={()=>{navigate('/signup')}}>LOGOUT</button>
    </div>
    </>
  )
}

export default PoolHome
