import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function PoolForm() {
  const loggedinUser = parseInt(localStorage.getItem("logged"));

    const [values, setValues] = useState({
        name: "",
        start: "",
        end: "",
        date:"",
        time:"",
        seats:"",
        poolById:loggedinUser
        
      });
      use
      // const [error, setError] = useState('');
      const navigate = useNavigate();
    
    //   const notify = ()=>{
     
    //     toast.error(error,{position: toast.POSITION.TOP_CENTER})
    // }
    
      const handleInput = (event) => {
        console.log(values);
        setValues((prev) => ({
          ...prev,
          [event.target.name]: [event.target.value],
        }));
        console.log(event.target.name);
    
        console.log(event.target.value);
      };
    
      const handleSubmit = (event) => {
        event.preventDefault();
        axios
          .post("http://localhost:8081/poolform", values)
          .then((res) => {
            navigate("/poolhome");
          })
          .catch(error => {
            console.log(error.response.data);
            // setError(error.response.data.error);
            toast.error(error.response.data.error, {
              position: "top-center",
            })
          });
      };
    
  return (
   <>
    <form action="" onSubmit={handleSubmit}>
  <div class="form-group">
    <label for="exampleFormControlInput1">Pool By</label>
    <input type="text" class="form-control" id="exampleFormControlInput1" placeholder="Enter your name" name="name" onChange={handleInput} />
  </div>

  <div class="form-group">
    <label for="exampleFormControlInput1">Pool From</label>
    <input type="text" class="form-control" id="exampleFormControlInput1" placeholder="Enter Pool start location" name="start" onChange={handleInput} />
  </div>

  <div class="form-group">
    <label for="exampleFormControlInput1">Pool To</label>
    <input type="text" class="form-control" id="exampleFormControlInput1" placeholder="Enter pool end location" name="end" onChange={handleInput} />
  </div>

  <div class="form-group">
    <label for="exampleFormControlInput1">Pool Date</label>
    <input type="text" class="form-control" id="exampleFormControlInput1" placeholder="Enter Pool Date" name="date" onChange={handleInput} />
  </div>

  <div class="form-group">
    <label for="exampleFormControlInput1">Pool Time</label>
    <input type="text" class="form-control" id="exampleFormControlInput1" placeholder="Enter Pool Time" name="time" onChange={handleInput} />
  </div>

  <div class="form-group">
    <label for="exampleFormControlInput1">Pool Total Seats</label>
    <input type="text" class="form-control" id="exampleFormControlInput1" placeholder="Enter the total seats available" name="time" onChange={handleInput} />
  </div>
  
  <button class="btn-btn primary" >Create Pool</button>

</form>
      <ToastContainer />

   </>
  )
}

export default PoolForm
