import axios from "axios";
import React, { useEffect, useState } from "react";
import './aboutpool.css';

function AllPool({ allPool }) {
  const loggedinUser = parseInt(localStorage.getItem("logged"));
  const [selectedRow, setSelectedRow] = useState(null);

  const [values, setValues] = useState({
    poolid: 0,
    loginid: loggedinUser,
  });

  const handleClick = (row) => {
    setSelectedRow(row);
  };
  console.log(allPool);
  useEffect(() => {
    if (selectedRow != null) {
      setValues({ poolid: selectedRow.poolId, loginid: loggedinUser });
      console.log(values);
    }
  }, [selectedRow]);

  const handleCheckIn = () => {
    axios
      .post("http://localhost:8081/checkin", values)
      .then((res) => {
        console.log(res);
      })
      .catch((error) => {
        console.log(error.response.data);
        // toast.error(error.response.data.error, {
        //   position: "top-center",
        // });
      });
  };

  return (
       <div className="container">
      <div className="pools">
        {allPool.map((item) => (
          <div key={item.id} className="pool" onClick={() => handleClick(item)}>
            <div className="pool-header">
              <b>Pool Created By:</b> {item.poolBy}
            </div>
            <div className="pool-body">
              <div>
                <b>Pool Starting From:</b> {item.poolFrom}
              </div>
              <div>
                <b>Pool Date:</b> {item.poolDate}
              </div>
            </div>
          </div>
        ))}
      </div>
      <div className="selected-pool">
        {selectedRow && (
          <div>
            <p>
              <b>Pool To:</b> {selectedRow.poolTo}
            </p>
            <p>
              <b>Pool Date:</b> {selectedRow.poolDate}
            </p>
            <p>
              <b>Pool By:</b> {selectedRow.poolBy}
            </p>
            <p>
              <b>Logged In User:</b> {loggedinUser}
            </p>
            <p>
              <b>Members:</b>
              {selectedRow.members.map((e) => (
                <div key={e}>{e}</div>
              ))}
            </p>
            {selectedRow.members.includes(loggedinUser) === true ? (
              <button disabled className="button disabled">
                You have already checked in
              </button>
            ) : selectedRow.availableSeats > 0 ? (
              <button onClick={handleCheckIn} className="button primary">
                Check In
              </button>
            ) : (
              <button disabled className="button disabled">
                Full
              </button>
            )}
          </div>
        )}
      </div>
    </div>  );
}

export default AllPool;
